async function craftCraftingTable(bot) {
  await bot.chat("I need to gather more wood logs to craft a crafting table.");

  // First, check the inventory for enough spruce logs
  let logsNeeded = 4 - bot.inventory.count(mcData.itemsByName["spruce_log"].id);
  while (logsNeeded > 0) {
    // Explore for spruce logs if not enough
    const log = await exploreUntil(bot, new Vec3(0, 1, 0), 60, () => {
      const spruceLog = bot.findBlock({
        matching: bot.registry.blocksByName["spruce_log"].id,
        maxDistance: 32
      });
      return spruceLog;
    });
    if (log) {
      // Mine the spruce log
      await mineBlock(bot, "spruce_log", 1);
      await bot.chat("Collected a spruce log.");
      logsNeeded--;
    } else {
      await bot.chat("Failed to find any more spruce logs.");
      break;
    }
  }

  // Convert the spruce logs into wooden planks
  await bot.chat("Now I will craft wooden planks.");
  await craftItem(bot, "oak_planks", Math.ceil((4 - bot.inventory.count(mcData.itemsByName["spruce_planks"].id)) / 4));

  // Finally, craft the crafting table
  await bot.chat("Crafting a crafting table now.");
  await craftItem(bot, "crafting_table", 1);
}

